mapper.select:
select count(1) from recommend
left join video_info on recommend.av = video_info.av

	and recommend.av = #{av}
	and recommend.status = #{status}
	and recommend.issue_id = #{issueId}
	and video_info.status = #{videoStatus}

select recommend.*, video_info.name, video_info.owner, video_info.external_owner, video_info.pub_time, video_info.type, video_info.bv, video_info.status as video_status from recommend left join video_info on recommend.av = video_info.av
and recommend.av = #{av} and recommend.status = #{status} and recommend.issue_id = #{issueId} and video_info.status = #{videoStatus}
order by [${sorter}] ${sorted}
order by id desc
offset ${start} rows fetch next ${pageSize} rows only

mapper.insert:
insert into recommend (
av, status, text, operator, issue_id, start_time, end_time, sort_num, create_time, update_time,
) values (
#{av}, #{status}, #{text}, #{operator}, #{issueId}, #{startTime}, #{endTime}, #{sortNum}, getdate(), getdate(),
)
