mapper.select:
select count(1) from recommend

	and recommend.av = #{av}
	and recommend.status = #{status}
	and recommend.issue = #{issue}

select * from recommend

	and recommend.av = #{av}
	and recommend.status = #{status}
	and recommend.issue = #{issue}


	order by [${sorter}] ${sorted}


	order by id desc


	offset ${start} rows fetch next ${pageSize} rows only

mapper.insert:
insert into recommend (
av, status, text, operator, create_time, update_time,
) values (
#{av}, #{status}, #{text}, #{operator}, getdate(), getdate(),
)

mapper.update:
update recommend
update_time = getdate(), av = #{av}, status = #{status}, text = #{text}, operator = #{operator}, issue = #{issue},
where id = #{id}
