mapper.select:
select count(1) from recommend_talk

	and recommend_talk.id = #{id}
	and recommend_talk.status = #{status}
	and recommend_talk.type = #{type}
	and recommend_talk.issue_id = #{issueId}

select * from recommend_talk

	and recommend_talk.id = #{id}
	and recommend_talk.status = #{status}
	and recommend_talk.type = #{type}
	and recommend_talk.issue_id = #{issueId}


	order by [${sorter}] ${sorted}


	order by id desc


	offset ${start} rows fetch next ${pageSize} rows only

mapper.insert:
insert into recommend_talk (
speaker, text, expression, status, type, issue_id, value, create_time, update_time,
) values (
#{speaker}, #{text}, #{expression}, #{status}, #{type}, #{issueId}, #{value}, getdate(), getdate(),
)

mapper.update:
update recommend_talk
speaker = #{speaker}, text = #{text}, expression = #{expression}, type = #{type}, status = #{status}, remark = #{remark}, issue_id = #{issueId}, value = #{value}, update_time = getdate(),
where id = #{id}
