mapper.select:
select count(1) from task


		and task.id = #{id}


		and task.av = #{av}


		and task.status = #{status}


		and task.type = #{type}


		and task.batch_id = #{batchId}

select task.status, count(1) as number from task


		and task.id = #{id}


		and task.av = #{av}


		and task.type = #{type}


		and task.batch_id = #{batchId}


group by task.status

select * from task


		and task.id = #{id}


		and task.av = #{av}


		and task.status = #{status}


		and task.type = #{type}


		and task.batch_id = #{batchId}



	order by [${sorter}] ${sorted}


	order by id desc


	offset ${start} rows fetch next ${pageSize} rows only

mapper.insert:
insert into task (
av, create_time, update_time, status, type, batch_id,
) values (
#{av}, getdate(), getdate(), #{status}, #{type}, #{batchId},
)

mapper.update:
update task
update_time = getdate(), type = #{type}, status = #{status}, remark = #{remark}, batch_id = #{batchId},
where id = #{id}
