mapper.select:
select count(1) from users


		and users.id = #{id}


		and users.name = #{name}


		and users.email = #{email}


		and users.phone = #{phone}


		and task.status = #{status}


		and task.type = #{type}

select * from users


		and users.id = #{id}


		and users.name = #{name}


		and users.email = #{email}


		and users.phone = #{phone}


		and task.status = #{status}


		and task.type = #{type}



	order by [${sorter}] ${sorted}


	order by id desc


	offset ${start} rows fetch next ${pageSize} rows only

mapper.insert:
insert into users (
status, type, name, password, email, phone, last_login_time, create_time, update_time,
) values (
#{status}, #{type}, #{name}, #{password}, #{email}, #{phone}, #{lastLoginTime}, getdate(), getdate(),
)

mapper.update:
update users
name = #{name}, password = #{password}, email = #{email}, phone = #{phone}, type = #{type}, status = #{status}, last_login_time = #{lastLoginTime}, update_time = getdate(),
where id = #{id}
