mapper.select:
select count(1) from video_info


		and video_info.av = #{av}


		and video_info.is_delete = #{isDelete}


		and video_info.name like concat('%', #{name}, '%')


		and video_info.owner like concat('%', #{owner}, '%')


		and video_info.bv = #{bv}


		and video_info.type = #{type}


		and video_info.copyright = #{copyright}


		and video_info.status = #{status}

	-- 大于
		and create_time >= convert(varchar(10),#{createTimeStart},120)

	-- 小于
		and create_time < convert(varchar(10),#{createTimeEnd},120)

select * from video_info


		and video_info.av = #{av}


		and video_info.is_delete = #{isDelete}


		and video_info.name like concat('%', #{name}, '%')


		and video_info.owner like concat('%', #{owner}, '%')


		and video_info.bv = #{bv}


		and video_info.type = #{type}


		and video_info.copyright = #{copyright}


		and video_info.status = #{status}

	-- 大于
		and create_time >= convert(varchar(10),#{createTimeStart},120)

	-- 小于
		and create_time < convert(varchar(10),#{createTimeEnd},120)



	order by [${sorter}] ${sorted}


	order by pub_time desc


	offset ${start} rows fetch next ${pageSize} rows only

mapper.insert:
insert into video_info (
av, [name], img, [type], [owner], copyright, pub_time, start_time, is_delete, bv, [description], [state], [attribute], duration, mission_id, [dynamic], start_page, status, create_time, update_time,
) values (
#{av}, #{name}, #{img}, #{type}, #{owner}, #{copyright}, #{pubTime}, #{startTime}, #{isDelete}, #{bv}, #{description}, #{state}, #{attribute}, #{duration}, #{missionId}, #{dynamic}, #{startPage}, #{status}, getdate(), getdate(),
)

mapper.update:
update video_info
name = #{name}, img = #{img}, type = #{type}, [owner] = #{owner}, copyright = #{copyright}, pub_time = #{pubTime}, start_time = #{startTime}, is_delete = #{isDelete}, bv = #{bv}, [description] = #{description}, state = #{state}, attribute = #{attribute}, duration = #{duration}, mission_id = #{missionId}, [dynamic] = #{dynamic}, start_page = #{startPage}, status = #{status}, update_time = getdate(),
where av = #{av}

mapper.select:
select convert(varchar(10),create_time,120) as time, count(1) as number from video_info
and is_delete = #{isDelete}
and status = #{status}
-- 大于 and create_time >= convert(varchar(10),#{createTimeStart},120)
-- 小于 and create_time < convert(varchar(10),#{createTimeEnd},120)
group by convert(varchar(10),create_time,120) order by convert(varchar(10),create_time,120)
